import React, { useState } from 'react'

// 方块组件
function Square(props) {
  return <button className='square' onClick={props.onSquareClick}>{props.value}</button>
}

// 游戏板组件
export default function Board() {
  // 使用 useState 创建一个状态变量 squares，初始值为一个包含 9 个 null 的数组
  // 使用 setSquares 函数更新 squares 的值
  const [squares, setSquares] = useState(Array(9).fill(null))

  // handleClick 函数会在用户点击方块时被调用
  // 它将当前点击的方块的值设置为 'X'，并更新状态变量 squares
  function handleClick() {
    const newSquares = squares.slice()
    newSquares[0] = 'X'
    setSquares(newSquares)
  }

  return (
    <div className='board'>
      <div className='board-row'>
        <Square value={squares[0]} onSquareClick={this.handleClick}/>
        <Square value={squares[1]} />
        <Square value={squares[2]} />
      </div>
      <div className='board-row'>
        <Square value={squares[3]} />
        <Square value={squares[4]} />
        <Square value={squares[5]} />
      </div>
      <div className='board-row'>
        <Square value={squares[6]} />
        <Square value={squares[7]} />
        <Square value={squares[8]} />
      </div>
    </div>
  )
}
